function define_type() {
    //top
    if (X >= -3 && X < 3 &&
        Y >= -2.5 && Y < 4.5 &&
        Z >= 7 && Z < 11
    ) type = 1;
    else
        if (X >= 4 && X < 12 &&
            Y >= -2 && Y < 6 &&
            Z >= -4 && Z < 7
        ) type = 2;
        else
            if (X >= 9 && X < 12 &&
                Y >= -3 && Y < 3 &&
                Z >= -3 && Z < 1
            ) type = 3;

            //left
            else
                if (X >= -1 && X < 2 &&
                    Y >= 3 && Y < 8 &&
                    Z >= 4 && Z < 9
                ) type = 4;
                else
                    if (X >= -1 && X < 2 &&
                        Y >= 8 && Y < 12 &&
                        Z >= -6 && Z < 5
                    ) type = 5;
                    else
                        //behind
                        if (X >= -9 && X < -3 &&
                            Y >= -3 && Y < 3 &&
                            Z >= 4 && Z < 7
                        ) type = 6;
                        else
